import React, { useState } from 'react'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom'
import { HomeOutlined, UserOutlined } from '@ant-design/icons'
import { Menu } from 'antd'
import classes from './MenuHome.module.css'

const { SubMenu } = Menu
const MenuHome = () => {
  const [currentPage, setCurrentPage] = useState('home')

  const handleMenuItemPress = (e) => {
    setCurrentPage(e.key)
  }
  const BecomeATeacher = () => {
    return <h2>BecomeATeacher</h2>
  }
  const Home = () => {
    return (
      <Menu
        selectedKeys={[currentPage]}
        mode="horizontal"
        onClick={handleMenuItemPress}
        style={{ backgroundColor: 'transparent', color: 'white' }}
      >

        <Menu.Item
          style={{
            color: 'white',
          }}
          key="home"
          icon={<HomeOutlined />}
          onClick={() => { console.log('home') }}
        >
          <Link to="/home">
            <h5>
              Home
            </h5>
          </Link>
        </Menu.Item>
        <Menu.Item key="app" icon={<HomeOutlined />}>
          <Link to="/teacher">
            Teacher
          </Link>
        </Menu.Item>
        <SubMenu
          key="SubMenu"
          icon={<HomeOutlined />}
          title="Courses"
        >
          <Menu.ItemGroup title="Item 1">
            <Menu.Item key="setting:1">Option 1</Menu.Item>
            <Menu.Item key="setting:2">Option 2</Menu.Item>
          </Menu.ItemGroup>
          <Menu.ItemGroup title="Item 2">
            <Menu.Item key="setting:3">Option 3</Menu.Item>
            <Menu.Item key="setting:4">Option 4</Menu.Item>
          </Menu.ItemGroup>
        </SubMenu>
        <Menu.Item key="account" icon={<UserOutlined />}>
          <Link to="/account">
            Account
          </Link>
        </Menu.Item>
      </Menu>
    )
  }

  const Account = () => {
    return <h2>Account</h2>
  }
  const Courses = () => {
    return <h2>Courses</h2>
  }
  // const listMenu = [
  //   {
  //     id: 1,
  //     title: 'Home',
  //     link: '/',
  //   },
  //   {
  //     id: 2,
  //     title: 'Courses',
  //     link: '/courses',
  //   },
  //   {
  //     id: 3,
  //     title: 'Become a teacher',
  //     link: '/becomeateacher',

  //   },
  //   {
  //     id: 4,
  //     title: 'Account',
  //     link: '/account',
  //   },
  // ]
  return (
    <Router>
      <div className={classes.AppHeaderMenu}>

        <Switch>
          <Route path="/" component={Home} />
          <Route path="/courses" component={Courses} />
          <Route path="/becomeateacher" component={BecomeATeacher} />
          <Route path="/account" component={Account} />
        </Switch>
      </div>
    </Router>
  )
}
export default MenuHome
