/* eslint-disable no-unused-vars */
import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Modal from 'react-bootstrap/Modal'
import styles from './ModalCreateCourse.module.scss'
import ModalComponent from '../../Account/ModalComponent/ModalComponent'
import deleteIcon from '../../../assets/images/delete.svg'
import name from '../../../assets/images/name.png'
import description from '../../../assets/images/description.png'

const ModalCreateCourse = (props) => {
  const { onCloseModalClick } = props
  const [isModalShow, setIsModalShow] = useState('')
  const [textModal, setTextModal] = useState('')
  const [tenBaiGiang, setTenBaiGiang] = useState('')
  const [fileInput, setFileInput] = useState('')
  const token = useSelector((value) => value?.userReducer?.token)
  const [moTa, setMoTa] = useState('')

  const dispatch = useDispatch()

  const handleTenBaiGiangChange = (event) => {
    setTenBaiGiang(event.target.value)
  }
  const handleMoTaChange = (event) => {
    setMoTa(event.target.value)
  }
  const handleFileInputChange = (event) => {
    setFileInput(event.target.value)
    console.log('===============================================')
    console.log('fileInput', fileInput)
    console.log('===============================================')
  }

  const handleCreateCourseClick = () => {
    setTextModal('Create course ok')
    setIsModalShow(true)
  }

  const handleModalComponentCloseClick = () => {
    setIsModalShow(false)
  }
  return (
    <div className={styles.container}>
      {isModalShow && <Modal
        show
        centered
        backdrop
        bsPrefix="modal"
      >
        <ModalComponent
          textModal={textModal}
          handleModalComponentCloseClick={handleModalComponentCloseClick}
        />
      </Modal>}

      <div className={styles.coverT}>
        <p className={styles.title}>Create new course</p>
        <p className={styles.description}> Your course will be vetify as soon as possible</p>
        <div className={styles.divInput}>
          <div className={styles.coverImage}>
            <img
              src={name}
              alt="logo"
            />
          </div>
          <form
            className={styles.formInput}
            onSubmit={(e) => { e.preventDefault() }}
          >
            <input
              className={styles.input}
              placeholder="Course name"
              formNoValidate
              value={tenBaiGiang}
              onChange={handleTenBaiGiangChange}
            />
          </form>
        </div>
        <div className={styles.divInput}>
          <div className={styles.coverImage}>
            <img
              src={description}
              alt="logo"
            />
          </div>
          <form
            className={styles.formInput}
            onSubmit={(e) => { e.preventDefault() }}
          >
            <input
              className={styles.input}
              placeholder="Description"
              formNoValidate
              value={moTa}
              onChange={handleMoTaChange}
            />
          </form>
        </div>
        <div className={styles.divInput}>
          <span>Video source: </span>
          <form
            className={styles.formInput}
            onSubmit={(e) => { e.preventDefault() }}
          >
            <input
              className={styles.input}
              type="file"
              value={fileInput}
              onChange={handleFileInputChange}
            />
          </form>
        </div>

        <button
          className={styles.buttonSignUp}
          onClick={handleCreateCourseClick}
          type="button"
        >
          <p>Create course</p>
        </button>
        <p className={styles.textLicense}>
          By clicking on Create course, you agree to our Terms of service and Privacy policy.
        </p>

        <button
          className={styles.buttonClose}
          type="button"
          onClick={onCloseModalClick}
        >
          <img
            src={deleteIcon}
            alt="logo"
          />
        </button>
      </div>
    </div>
  )
}
export default ModalCreateCourse
