export const ParseTimeToMinuteAndSecond = (time) => {
  if (time.hours > 0) {
    return `${time.minute + time.hours * 60} : ${time.seconds} `
  }
  return `${time.minute} : ${time.seconds} `
}
export const GetHourOfTime = (time) => {
  if (time.hours > 0) {
    return `${time.hours} : ${time.minute}`
  }
  return `${time.minute} `
}
