/* eslint-disable no-unused-vars */
import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { useHistory } from 'react-router-dom'
import styles from './Account.module.scss'
import AvatarMeng from '../../assets/images/AvatarMeng.svg'
import { userAction } from '../../redux/actions'
import phone from '../../assets/images/phone.png'
import Password from '../../assets/images/Password.svg'
import user from '../../assets/images/user.png'
import ProfileItem from '../../components/Account/ProfileItem/ProfileItem'

const Account = () => {
  const [valueName, setValueName] = useState('')
  const [valuePhoneNumber, setValuePhoneNumber] = useState('')
  const [valuePassword, setValuePassword] = useState('')
  const [valueConfirmPassword, setValueConfirmPassword] = useState('')
  const dispatch = useDispatch()
  const setValueNameChange = (event) => {
    setValueName(event.target.value)
  }
  const setValuePhoneChange = (event) => {
    setValuePhoneNumber(event.target.value)
  }
  const setValuePasswordChange = (event) => {
    setValuePassword(event.target.value)
  }
  const setValueConfirmPasswordChange = (event) => {
    setValueConfirmPassword(event.target.value)
  }

  // eslint-disable-next-line no-unused-vars
  const history = useHistory()

  const handleSignOutClick = () => {
    dispatch(userAction.LOGIN({
      token: null,
    }))
    history.push('/')
  }
  return (
    <div className={styles.container}>
      <div className={styles.divProfile}>
        <p className={styles.title}>EDIT PROFILE</p>
        <p className={styles.description}>Manage your Design+Code profile and account</p>
        <div className={styles.divAvatar}>
          <img
            src={AvatarMeng}
            alt="logo"
          />
          <button
            type="button"
          >
            CHANGE AVATAR
          </button>
        </div>
        <p className={styles.title}>PROFILE SETTINGS</p>
        <div className="row">
          <div className="col-lg-6">
            {/* <ProfileItem
              image={user}
              placeholder="Your name"
              value={valueName}
              onChange={setValueNameChange}
            /> */}
            <div className={styles.divInput}>
              <div className={styles.coverImage}>
                <img
                  src={phone}
                  alt="logo"
                />
              </div>
              <form
                className={styles.formInput}
                onSubmit={(e) => { e.preventDefault() }}
              >
                <input
                  className={styles.input}
                  placeholder="Phone number"
                  formNoValidate
                  value={valuePhoneNumber}
                  onChange={setValuePhoneChange}
                />
              </form>
            </div>
            <div className={styles.divInput}>
              <div className={styles.coverImage}>
                <img
                  src={Password}
                  alt="logo"
                />
              </div>
              <form
                className={styles.formInput}
                onSubmit={(e) => { e.preventDefault() }}
              >
                <input
                  className={styles.input}
                  placeholder="Password"
                  formNoValidate
                  value={valuePassword}
                  onChange={setValuePasswordChange}
                  type="password"
                />
              </form>
            </div>
            <div className={styles.divInput}>
              <div className={styles.coverImage}>
                <img
                  src={Password}
                  alt="logo"
                />
              </div>
              <form
                className={styles.formInput}
                onSubmit={(e) => { e.preventDefault() }}
              >
                <input
                  className={styles.input}
                  placeholder="Confirm password"
                  formNoValidate
                  value={valueConfirmPassword}
                  onChange={setValueConfirmPasswordChange}
                  type="password"
                />
              </form>
            </div>
          </div>
          <div className="col-lg-6" />
        </div>
        <button
          className={styles.buttonBig}
          type="button"
          onClick={handleSignOutClick}
        >
          Sign Out
        </button>
      </div>
    </div>
  )
}
export default Account
