import { userType } from '../types'

const initState = {
  token: '',
  accountState: 'SignIn',
  isModalShow: true,
}

const userReducer = (state = initState, action) => {
  const data = action?.payload?.data
  const { token } = data?.token
  switch (action.type) {
    case userType.LOGIN:
      return {
        ...state, token,
      }
    case userType.SET_IS_MODAL_SHOW:
      return {
        ...state, isModalShow,
      }
    case userType.SET_ACCOUNT_STATE:
      return {
        ...state, accountState,
      }
    default:
      return state
  }
}
