/* eslint-disable react/jsx-indent-props */
import React, { useState } from 'react'
import { ListGroup } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import CourseItem from '../../components/Courses/CourseItem/CourseItem'
import LogoFlutter from '../../assets/images/LogoFlutter.svg'
import AvatarMeng from '../../assets/images/AvatarMeng.svg'
import background1 from '../../assets/images/background1.svg'
import background12 from '../../assets/images/background12.svg'
import background8 from '../../assets/images/background8.svg'
import background9 from '../../assets/images/background9.svg'
import background10 from '../../assets/images/background10.svg'
import background11 from '../../assets/images/background11.svg'
import styles from './Courses.module.scss'

const arrayCourses = [
  {
    id: 1,
    title: 'React Native for Designer',
    description: '16 videos - 3.5 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background8,
    chude: 'react-native',
    author: 'mtl',

  },
  {
    id: 2,
    title: 'Build a full site in Webflow',
    description: '12 videos - 4 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background9,
    chude: 'react-native',
    author: 'phm',

  },
  {
    id: 3,
    title: 'Motion Design in After Effects',
    description: '10 videos - 1 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background10,
    chude: 'react-native',
    author: 'mtl',

  },
  {
    id: 4,
    title: 'Swift Advanced',
    description: '2 videos - 1 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background11,
    chude: 'lutter',
    author: 'nna',

  },
  {
    id: 5,
    title: 'Learn Sketch',
    description: '2 videos - 1 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background12,
    chude: 'designer',
    author: 'dvd',

  },
  {
    id: 6,
    title: 'Learn Swift',
    description: '27 videos - 4 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background1,
    chude: 'designer',
    author: 'phm',

  },
  {
    id: 7,
    title: 'Learn Swift',
    description: '27 videos - 4 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background1,
    chude: 'designer',
    author: 'mtl',

  },
  {
    id: 8,
    title: 'Learn Swift',
    description: '27 videos - 4 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background1,
    chude: 'designer',
    author: 'nna',

  },
  {
    id: 9,
    title: 'Learn Swift',
    description: '27 videos - 4 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background1,
    chude: 'reactJS',
    author: 'phm',

  },
]
const categories = [
  {
    id: 1,
    chude: 'react-native',
    title: 'React Native',
  },
  {
    id: 2,
    chude: 'designer',
    title: 'Designer',
  },
  {
    id: 3,
    chude: 'reactJS',
    title: 'ReactJS',
  },
  {
    id: 4,
    chude: 'flutter',
    title: 'Flutter',
  },
]
const authors = [
  {
    id: 1,
    value: 'mtl',
    name: 'Mạnh Thiên Lý',
  },
  {
    id: 2,
    value: 'dvd',
    name: 'David Dang',
  },
  {
    id: 3,
    value: 'nna',
    name: 'Nguyễn Nhật Ánh',
  },
  {
    id: 4,
    value: 'phm',
    name: 'Phạm Huy Mạnh',
  },
]

const Courses = () => {
  const [filterAuthor, setFilterAuthor] = useState('')
  const [chuDe, setChuDe] = useState('')
  const handleFilterAuthorChange = (event) => {
    setFilterAuthor(event.target.value)
  }
  const listChuDe = categories.map((item) => <ListGroup.Item as="li">
    <Link
      onClick={() => { setChuDe(item.chude) }}
      to={`/courses/${item.chude}`}
    >
      <div className={styles.MenuItem}>
        <p>{item.title}</p>
      </div>
    </Link>
  </ListGroup.Item>)
  const listAuthors = authors
    .map((item) => <option value={item.value}>{item.name}</option>)

  const filterItems = () => {
    if (!chuDe) {
      if (!filterAuthor) {
        // All
        return arrayCourses.map((item) => <CourseItem
          title={item.title}
          description={item.description}
          customStyles={{
            marginTop: 30,
            marginLeft: 50,
          }}
          logo={item.logo}
          avatar={item.avatar}
          background={item.background}
        />)
      }
      // filter by authors
      return arrayCourses
        .filter((i) => i.author === filterAuthor)
        .map((item) => <CourseItem
          title={item.title}
          description={item.description}
          customStyles={{
            marginTop: 30,
            marginLeft: 50,
          }}
          logo={item.logo}
          avatar={item.avatar}
          background={item.background}
        />)
    }
    // filter by  categories
    if (!filterAuthor) {
      return arrayCourses
        .filter((i) => i.chude === chuDe)
        .map((item) => <CourseItem
          title={item.title}
          description={item.description}
          customStyles={{
            marginTop: 30,
            marginLeft: 50,
          }}
          logo={item.logo}
          avatar={item.avatar}
          background={item.background}
        />)
    }
    // filter by both
    return arrayCourses
      .filter((i) => i.chude === chuDe && i.author === filterAuthor)
      .map((item) => <CourseItem
        title={item.title}
        description={item.description}
        customStyles={{
          marginTop: 30,
          marginLeft: 50,
        }}
        logo={item.logo}
        avatar={item.avatar}
        background={item.background}
      />)
  }
  const listItems = filterItems()
  return (
    <div className={styles.container}>
      <div className={styles.menuChuDe}>
        <ListGroup as="ul">
          <ListGroup.Item as="li">
            <p className={styles.categoryTitle}>Categories</p>
          </ListGroup.Item>
          <ListGroup.Item as="li">
            <Link
              onClick={() => { setChuDe('') }}
              to="/courses"
            >
              <div className={styles.MenuItem}>
                <p>All courses</p>
              </div>
            </Link>
          </ListGroup.Item>
          {listChuDe}
        </ListGroup>

      </div>
      <div className={styles.courses}>
        <div className={styles.divFilter}>
          <p>Filter by authors</p>
          <select
            value={filterAuthor}
            onChange={handleFilterAuthorChange}
          >
            <option value="">All</option>
            {listAuthors}

          </select>
        </div>
        <ul className="row" style={{ paddingLeft: 15 }}>
          {listItems}
        </ul>
      </div>

    </div>

  )
}
export default Courses
