import { userType } from '../types'

const initState = {
  token: '',
  accountState: true,
}

const userReducer = (state = initState, action) => {
  const data = action?.payload?.data
  const token = data?.token
  switch (action.type) {
    case userType.GET_USER_SUCCESS:
      return {
        ...state,
      }
    case userType.LOGIN:
      return {
        ...state, token,
      }
    default:
      return state
  }
}
export default userReducer
