import React, { useState, useEffect } from 'react'
import Modal from 'react-bootstrap/Modal'
import { useHistory } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import CourseItem from '../../components/Courses/CourseItem/CourseItem'
import itemCourseImage from '../../assets/images/itemCourseImage.svg'
import LogoWhite from '../../assets/images/LogoWhite.svg'
import CreateCourseComponent from '../../components/UploadCourse/CreateCourseComponent'
import AvatarMeng from '../../assets/images/AvatarMeng.svg'
import background3 from '../../assets/images/background3.svg'
import background4 from '../../assets/images/background4.svg'
import background5 from '../../assets/images/background5.svg'
import background6 from '../../assets/images/background6.svg'
import backgroundUpload from '../../assets/images/backgroundUpload.svg'
import styles from './UploadCourse.module.scss'
import { coursesAction } from '../../redux/actions'

const UploadCourse = () => {
  const [isModalShow, setModalShow] = useState(false)
  const token = useSelector((value) => value?.userReducer?.token)
  // eslint-disable-next-line no-unused-vars
  const dispatch = useDispatch()
  const history = useHistory()
  useEffect(() => {
    dispatch(coursesAction.GET_UPLOADED_VIDEOS({ token }))
  })
  const handleCloseModal = () => {
    setModalShow(false)
  }
  const handleShowModal = () => {
    setModalShow(true)
  }
  const handleOnClick = (item) => {
    history.push({
      pathname: 'create-new-coruse',
      state:
      {
        maKhoaHoc: item.id,
        token,
      },
    })
  }
  const numbers = [
    {
      title: 'Flutter for Designers',
      description: '20 videos - 3 hours',
      logo: LogoWhite,
      avatar: AvatarMeng,
      totalView: '12354',
      gia: '99.99',
      background: itemCourseImage,
    },
    {
      title: 'SwiftUI Livestreams',
      description: '20 videos - 3 hours',
      logo: LogoWhite,
      avatar: AvatarMeng,
      totalView: '12354',
      gia: '99.99',
      background: background3,
    },
    {
      title: 'Design System in Figma',
      description: '20 videos - 3 hours',
      logo: LogoWhite,
      avatar: AvatarMeng,
      totalView: '12354',
      gia: '99.99',
      background: background4,
    },
    {
      title: 'UI Design for Developers',
      description: '20 videos - 3 hours',
      logo: LogoWhite,
      avatar: AvatarMeng,
      totalView: '12354',
      gia: '99.99',
      background: background5,
    },
    {
      title: 'React for Designers',
      description: '20 videos - 3 hours',
      logo: LogoWhite,
      avatar: AvatarMeng,
      totalView: '12354',
      gia: '99.99',
      background: background6,
    },

  ]
  const listItems = numbers.map((item) => <CourseItem
    title={item.title}
    description={item.description}
    customStyles={{ margin: 20 }}
    logo={item.logo}
    avatar={item.avatar}
    background={item.background}
    totalView={item.totalView}
    gia={item.gia}
    onClick={() => handleOnClick(item)}
  />)
  return (
    <div className={styles.container}>
      {isModalShow && <Modal
        show
        backdrop
        bsPrefix="modal"
      >
        <CreateCourseComponent
          onCloseModalClick={handleCloseModal}
        />
      </Modal>}
      <ul className="row">
        <CourseItem
          title="Create a new course"
          description="Long time no see. Create a new course now. "
          customStyles={{ margin: 20 }}
          background={backgroundUpload}
          onClick={handleShowModal}
        />
        {listItems}
      </ul>
    </div>
  )
}
export default UploadCourse
