/* eslint-disable react/jsx-indent-props */
import React, { useState } from 'react'
import { ListGroup } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import CourseItem from '../../components/Courses/CourseItem/CourseItem'
import LogoFlutter from '../../assets/images/LogoFlutter.svg'
import AvatarMeng from '../../assets/images/AvatarMeng.svg'
import background1 from '../../assets/images/background1.svg'
import background12 from '../../assets/images/background12.svg'
import background8 from '../../assets/images/background8.svg'
import background9 from '../../assets/images/background9.svg'
import background10 from '../../assets/images/background10.svg'
import background11 from '../../assets/images/background11.svg'
import styles from './Courses.module.scss'

const numbers = [
  {
    id: 1,
    title: 'React Native for Designer',
    description: '16 videos - 3.5 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background8,
  },
  {
    id: 2,
    title: 'Build a full site in Webflow',
    description: '12 videos - 4 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background9,
  },
  {
    id: 3,
    title: 'Motion Design in After Effects',
    description: '10 videos - 1 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background10,
  },
  {
    id: 4,
    title: 'Swift Advanced',
    description: '2 videos - 1 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background11,
  },
  {
    id: 5,
    title: 'Learn Sketch',
    description: '2 videos - 1 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background12,
  },
  {
    id: 6,
    title: 'Learn Swift',
    description: '27 videos - 4 hours',
    logo: LogoFlutter,
    avatar: AvatarMeng,
    background: background1,
  },
]
const chude = [
  {
    id: 1,
    chude: 'react-native',
    title: 'React Native',
  },
  {
    id: 2,
    chude: 'designer',
    title: 'Designer',
  },
  {
    id: 3,
    chude: 'reactJS',
    title: 'ReactJS',
  },
  {
    id: 4,
    chude: 'flutter',
    title: 'Flutter',
  },
]

const Courses = () => {
  const [chuDe, setChuDe] = useState('')
  const listChuDe = chude.map((item) => <ListGroup.Item as="li">
    <Link
      onClick={() => { setChuDe(item.chude) }}
      to={`/couses/${item.chude}`}
    >
      <div className={styles.MenuItem}>
        <p>{item.title}</p>
      </div>
    </Link>
  </ListGroup.Item>)

  const filterChuDe = () => {
    const listItems = !chuDe ? numbers.map((item) => <CourseItem
      title={item.title}
      description={item.description}
      customStyles={{
        marginTop: 30,
        marginLeft: 50,
      }}
      logo={item.logo}
      avatar={item.avatar}
      background={item.background}
    />)
      : numbers.filter((i) => i.chude === chuDe).map((item) => <CourseItem
        title={item.title}
        description={item.description}
        customStyles={{
          marginTop: 30,
          marginLeft: 50,
        }}
        logo={item.logo}
        avatar={item.avatar}
        background={item.background}
      />)
    return listItems
  }
  const listItems = filterChuDe()
  return (
    <div className="row">
      <div className="col-lg-2">
        <ListGroup as="ul">
          {listChuDe}
        </ListGroup>
      </div>
      <div className="col-lg-10">
        <ul className="row">
          {listItems}
        </ul>
      </div>

    </div>

  )
}
export default Courses
