/* eslint-disable react/jsx-indent-props */
import React from 'react'
import { Link } from 'react-router-dom'
import styles from './CourseItem.module.scss'

const CourseItem = (props) => {
  const {
    customStyles,
    title,
    description,
    avatar,
    logo,
    background,
    onClick,
    totalView,
    gia,
    totalVideos,
    to,
    totalTimes,
  } = props

  return (

    to
      ? <Link
        to={to}
        className={styles.container}
        style={customStyles || { margin: 0 }}
        onClick={onClick && onClick}
      >
        {background && <img src={background} className={styles.background} alt="logo" />}
        {logo && <img src={logo} className={styles.logo} alt="logo" />}
        {avatar && <img src={avatar} className={styles.author} alt="logo" />}
        <p className={styles.title}>{title}</p>
        <p className={styles.description}>{description}</p>
        <p className={styles.description}>{`${totalVideos} videos - ${totalTimes}`}</p>
        {totalView && <p className={styles.totalView}>{totalView}</p>}
        {gia === 0
          ? <p className={styles.gia}>Free</p>
          : <p className={styles.gia}>{`$ ${gia}`}</p>}

      </Link>
      : <Link
        className={styles.container}
        style={customStyles || { margin: 0 }}
        onClick={onClick && onClick}
      >
        {background && <img src={background} className={styles.background} alt="logo" />}
        {logo && <img src={logo} className={styles.logo} alt="logo" />}
        {avatar && <img src={avatar} className={styles.author} alt="logo" />}
        <p className={styles.title}>{title}</p>
        <p className={styles.description}>{description}</p>
        <p className={styles.description}>{`${totalVideos} videos - ${totalTimes}`}</p>
        {totalView && <p className={styles.totalView}>{`${totalView} sold`}</p>}
        {gia === 0
          ? <p className={styles.gia}>Free</p>
          : <p className={styles.gia}>{`$ ${gia}`}</p>}
      </Link>

  )
}
export default CourseItem
